void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 cam = texture(iChannel0, uv).xyz;
    float waveHeight = (texture(iChannel1, vec2(uv.x, 0.75)).x);
    vec3 col = vec3(0);
    if(uv.y > 0.5){col = vec3(texture(iChannel1, vec2(uv.x, 0.25)).x);}
    if( abs( 0.5*(waveHeight) - (uv.y) ) < 0.002){col = vec3(1.0);}

    // Output to screen
    fragColor = vec4(col,1.0);
}
