float plot (vec2 pos,float yval)
{
    return smoothstep(pos.x - 0.6, pos.x, yval) -
    smoothstep(pos.x, pos.x + 0.4, yval);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float val = abs(sin(uv.y * 5. + iTime * 1.));

    val = plot(uv, val);

    // Time varying pixel color
    vec3 col = (1. - val) * vec3(0) + val * (cos(iTime+2. * uv.xyx + vec3(1,2,cos(iTime) * 2.))*0.5 + 1.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
