// Many thanks to Patricio Gonzalez Vivo & Jen Lowe for their tutorials on thebookofshaders.com
// Their website is an invaluable resource when starting out with learning shaders and has helped me greatly


// Random function made by Patricio Gonzalez Vivo
// taken from https://thebookofshaders.com/11/
float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                * 43758.5453123);
}

// Value noise made by Inigo Quilez - iq/2013
// taken from https://www.shadertoy.com/view/lsf3WH
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    vec2 u = f*f*(3.0-2.0*f);
    return mix( mix( random( i + vec2(0.0,0.0) ),
                     random( i + vec2(1.0,0.0) ), u.x),
                mix( random( i + vec2(0.0,1.0) ),
                     random( i + vec2(1.0,1.0) ), u.x), u.y);
}


// Line function taken from here: https://thebookofshaders.com/edit.php#11/wood.frag
float line (vec2 coord, float b, float scale)
{
    coord *= scale;
    return smoothstep(0.0, 0.5 + b * 5.0, abs((sin(coord.x * 3.14159) + b * 2.0 ) * 0.5));
}

mat2 rotate(float angle)
{
    return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 nCoord = fragCoord.xy/iResolution.xy;

    nCoord += vec2(0.5, 1.3);
    nCoord = rotate(45.0 * noise(nCoord.xy) + (iTime * 0.5)) * nCoord;

    float fColor = noise(nCoord);

    fragColor =  vec4((vec3(line(vec2(noise(nCoord.xy) + (iTime * 0.05)), 0.2, 5.0))), noise(nCoord.xy + iTime));
}
