#define PI 3.14159255359

vec2 rotate2D(vec2 st,float ang){
    mat3 rotate = mat3(cos(ang),-sin(ang),1.0,sin(ang),cos(ang),1.0,0.0,0.0,1.0);
    vec3 r = rotate * vec3(st,1.0);
    return vec2(r.x,r.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;

    uv=fract(uv*2.)-0.5;

    uv*=floor(sin(iTime)*10.);

    float index = 0.0;
    index+=step(1.0,mod(uv.x,2.0));
    index+=step(1.0,mod(uv.y,2.0))*2.0;

    uv = rotate2D(uv,PI*iTime*(index+1.));

    // Output to screen
    fragColor = vec4(vec3(step(uv.x,uv.y)),1.0);
}
