void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( u+u - R ) /R.y,
         G = exp2(-4.*U*U);
    O = dot(U,U)<.7 ? vec4(G.x*G.y)     //  white = 2D Gaussian
                    : vec4(G,0,0);      //  red, green = 1D Gaussians

    float s = sin(50.*max(O.r,O.g));    // isovalues
    O = mix( O, vec4(0,0,1,0), smoothstep(1.,-1.,abs(s)/fwidth(s)) ); // blend
}
