void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;

    O =  .5 + 4.*( textureLod(iChannel0, U, 1.).rrrr            // HF of texture 0
                  -textureLod(iChannel0, U, 0.).rrrr )

       +  .25*textureLod(iChannel1, U/vec2(6,1), 1.).rrrr;      // LF of texture 1


}
