void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //normalized coordinate plain [black]
    vec2 uv = 10.* (fragCoord  - .5 * iResolution.xy ) / iResolution.y;
    float a = 0.;

    //function f(x)
    float f = -1. * sin(uv.x-18.*iMouse.x/iResolution.x+9.); //customize (coef. = about 18)
    a = f + uv.y;
    fragColor = vec4( 1. - abs(a)/fwidth(a) );

    //Ox & Oy
     if((uv.y<.01&&uv.y>-.01)||(uv.x<.01&&uv.x>-.01))
        fragColor = vec4 (.2,.2,.2,1.);
}
