float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y - vec2(0.375,0.);
   // uv += vec2(0.125 * cos(iTime),0.);
//uv += vec2(0.05 * iTime,0.)
uv = floor(99. * uv) / 99.;

float t= iTime;
//float b = 0.5 * (1. + cos(t));
vec2 dir = uv - 0.5;
dir = fract(5. * dir +  0.5) - 0.5;
float theta = atan(dir.y,dir.x);

float k = 2. + 2. * cos(sin( theta) + t * (0.2 + abs(random(floor(uv * 50.)))));
float d = pow(abs(dir.x) * k,k) + pow(abs(dir.y) * k,k);

//d += 0.1 * (1. + cos(t + 3.14159));
float c= step(d,0.3);

float k2 = 2. + 2. * cos(theta + t * (0.2 + abs(random(floor((uv + 0.625) * 50.)))));
float d2 = pow(abs(dir.x) * k2, k2) + pow(abs(dir.y) * k2,k2);
float c2 =step(d2,0.3);

float k3 = 2. + 2. * cos(theta + t * (0.2 + abs(random(floor((uv - 0.125) * 50.)))));
float d3 = pow(abs(dir.x) * k3, k3) + pow(abs(dir.y) * k3, k3);
float c3 = step(d3,0.3);
vec3 col = vec3(c,c2,c3);//vec3(min(c,c2),min(c2,c3),min(c3,c));

if (col.x == 0. && col.y == 0. && col.z == 0.)
col = vec3(34.,32.,52.) / 255.;


    // Output to screen
    fragColor = vec4(col,1.0);
}
