// inspired https://twitter.com/muse_energy/status/1480146727433043968

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)

// https://iquilezles.org/articles/distfunctions2d
float sdHexagram( in vec2 p, in float r )
{
    const vec4 k = vec4(-0.5,0.8660254038,0.5773502692,1.7320508076);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= 2.0*min(dot(k.yx,p),0.0)*k.yx;
    p -= vec2(clamp(p.x,r*k.z,r*k.w),r);
    return length(p)*sign(p.y);
}

float sdCross( in vec2 p, in vec2 b, float r ) 
{
    p = abs(p); p = (p.y>p.x) ? p.yx : p.xy;
    vec2  q = p - b;
    float k = max(q.y,q.x);
    vec2  w = (k>0.0) ? q : vec2(b.y-p.x,-k);
    return sign(k)*length(max(w,0.0)) + r;
}

void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,d=normalize(vec3((C-.5*r.xy)/r.y,1.));
    for(float i=0.,g=0.,e,t=iTime;++i<99.;){
        p=g*d;
        p.z-=5.;
        p=R(p,normalize(vec3(1,3,2)),t*.5);
        vec4 q=vec4(p,max(0.,sin(t*.5)));
        q.xzw=R(q.xzw,normalize(vec3(1,2,3)),min(0.,sin(t)));
        g+=e=
            log(
                exp(sdHexagram(q.xy,1.)) + 
                exp(sdCross(q.wz,vec2(2.,.5),.3))
            )-.5;
        O.xyz+=mix(vec3(1),H(atan(q.z,q.x)),.6)*.012/exp(i*i*e);
    }
    O*=O;
}
