vec2 f(vec2 x, float t) {
    return vec2(cos(x.x+sin(t*3.0))+x.y+sin(t*2.)/2.0+0.5, sin(x.y+t+6.28*iMouse.x/iResolution.x+sin(t*5.))+x.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.0)/iResolution.y*30.+vec2(50, 0);
    vec2 x = uv;
    for(int i = 0; i < 20; i++) {
        x = f(x, iTime/5.0);
    }
    float t = length(x)/100.0;
    fragColor = vec4(pow(vec3(t), vec3(0.4545, 0.6545, 0.8545*(1.-iMouse.y/iResolution.y))), 1.0);
}
