// basic box mask
// https://www.shadertoy.com/view/7dlyR7

// math from Unreal engine node BoxMask-3D
float boxMask(vec2 uv, vec2 pivot, vec2 bounds, float falloff)
{
	vec2 p = abs(uv - pivot);
	p = max(p - (bounds * 0.5), 0.);

	return 1.0 - min(length(p) / max(falloff, 0.00001), 1.0);
}

// rotate UVs
// from https://shadertoyunofficial.wordpress.com/2019/01/02/programming-tricks-in-shadertoy-glsl/
#define rot(a)      mat2( cos(a), -sin(a), sin(a), cos(a) )

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // UVs
	vec2 uv = fragCoord.xy/iResolution.yy;

    float aspect = iResolution.x / iResolution.y;

    vec2 box_position = vec2(0.5 * aspect, 0.5); // center of screen

    // rotate the box
    float rot = iTime;

    // foreground mouse
	vec2 p = fragCoord / iResolution.x;
    vec4 m = iMouse / iResolution.x;

    float mSens = 3.;
    vec2 mFrom = (p - m.xy) * mSens;
    vec2 mTo = (p-abs(m.zw)) * mSens;
    vec2 mAdd = mFrom - mTo;

    // rotate it
    box_position *= rot(rot - mAdd.x + mAdd.y);
    uv *= rot(rot - mAdd.x + mAdd.y);

    vec2 box_bounds = vec2(0.5, 0.5);
    float box_falloff = 0.1;

    float box = boxMask(uv, box_position, box_bounds, box_falloff);

    vec3 col = mix(vec3(0.,0.,0.), vec3(1., 1., 1.), box);

    // Output to screen
    fragColor = vec4(col,1.0);
}
