// Author: Sudospective
// Title: tfw pondering the orb

#ifdef GL_ES
precision mediump float;
#endif

#define PI 3.14159265359
#define TWO_PI 6.28318530718

float drawCircle(in vec2 _uv, vec2 center, float r, float fuzziness) {
    return 1.0 - smoothstep(r - fuzziness, r + fuzziness, length(_uv - center));
}

float drawPoly(in vec2 _uv, vec2 center, float r, int sides, float angle) {
    r *= 0.5;
    vec2 uv = _uv - center;
    float a = atan(uv.x, uv.y) + PI + angle;
    float l = TWO_PI / float(sides);
    float d = cos(floor(0.5 + a / l) * l - a) * length(uv) - r;
    return 1.0 - smoothstep(r - 0.002, r + 0.002, d);
}

float drawPedals(in vec2 _uv, vec2 center, float r, float loops, float fuzziness, float angle, float func) {
    vec2 uv = _uv - center;
    r = length(uv) / r;
    float a = atan(uv.x, uv.y) + angle;
    float f = abs(cos(a * loops));
    f *= func;
    return 1.0 - smoothstep(f - fuzziness, f + fuzziness, r);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy/iResolution.y;
    float scale = iResolution.x/iResolution.y;

    vec3 color = vec3(0.0);
    float zoom = 0.2 + tan(1.5766 + iTime * 0.125) * 0.002;
    float circ1 = drawCircle(uv, vec2(0.5 * scale, 0.5), 0.25 + zoom, 0.01);
    float circ2 = drawCircle(uv, vec2(0.5 * scale, 0.5), zoom * 0.01, 0.1);
    float circ3 = drawCircle(uv, vec2(0.5 * scale, 0.5), -0.005 + zoom * 0.01, 0.01);
    float poly = drawPoly(uv, vec2(0.5 * scale, 0.5), 0.2 + zoom, 6, -tan(mod(iTime, TWO_PI)) * 0.1);
    float pedal1 = drawPedals(uv, vec2(0.5 * scale, 0.5), 0.15 + zoom, 64.0, 0.5, mod(iTime, TWO_PI), 0.9 + 0.1 * sin(-PI * 0.5 + mod(iTime, TWO_PI)));
    float pedal2 = drawPedals(uv, vec2(0.5 * scale, 0.5), 0.25 + zoom, 128.0, 0.5,  -mod(iTime, TWO_PI), 0.9 + 0.1 * cos(mod(iTime, TWO_PI)));
    color.b += circ1;
    color.g += pedal1;
    color.b -= pedal2;
    color -= circ2;
    color -= poly * 0.1;
    color -= circ3;

    fragColor = vec4(color,1.0);
}
