vec2 sunPosition;
// clouds function
vec3 computeClouds(vec2 uv) {
if (uv.y < 0.5) { // if we are under the half part of the screen return black
return vec3(0.0, 0.0, 0.0);
}
else {
// compute ray direction
vec3 rd = normalize(vec3(uv*2.-1.,1.));
// add two layers of cloud
vec3 color = texture(iChannel0, rd.xz*.1/rd.y+iTime*.02).rrr;
color += texture(iChannel0, rd.xz*.05/rd.y-iTime*.03).rrr;
return color*.5;
}
}

vec3 sky(vec2 uv){
    vec3 col = vec3(1.1,1.1,1.1);
    vec3 blue = vec3(0.0,0.3,0.75);
    vec3 orange = vec3(1.0,0.85,0.1);
    float norme = distance(uv,sunPosition);
    vec3 noir = vec3(0.0,0.0,0.0);
    vec3 nuit = mix(noir,blue,(0.5-norme)*6.0);
    vec3 res = mix( mix(computeClouds(uv),nuit,uv.y).rgb, orange, (0.5-norme)*1.45);

    return res;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    sunPosition = vec2(cos(-iTime)/2.5 + 0.5,sin(-iTime)/2.5 +0.35);
    vec2 uv = fragCoord / iResolution.xy;
    vec2 uvBis = vec2(uv.x,1.0-uv.y);

    vec3 color = vec3(0.0, 0.0, 0.0);
    vec3 col = sky(uv);


    if(uv.y > 0.4)fragColor = vec4(col,1.0);

    if(uv.y < 0.4) fragColor = vec4(sky(uvBis),1.0);
    if(uv.y == 0.4) fragColor= vec4(mix(color,color,uv.x),1.0);



}
