// Loop that animates a superellipse
// https://en.wikipedia.org/wiki/Superellipse
// Made with help from: https://www.shadertoy.com/view/ll23DW

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Positioning
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y * 2.0;

    // Determine the radius bounds of the ellipse
    float strength = 3.0+sin(iTime * 2.0);

    // Create the superellipse
    float mask = pow(abs(uv.x),strength) + pow(abs(uv.y),strength);

    // Create the ellipse color
    vec3 color = smoothstep(1.0,0.98,mask) * sin(iTime / 5.0+uv.rgb+vec3(0,100,200));

    // Output to screen
    fragColor = vec4(color,1.0);
}
