#define PI 3.14159265359

mat2 Rotate2d(float angle)
{
	return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

float DrawCircle(vec2 st, vec2 center, float size)
{
	st += 0.5 - center;
	vec2 p = st - 0.5;
	return 1.0 - smoothstep(size - size * 0.01, size + size * 0.01, dot(p, p) * 4.0);
}

float DrawArc(vec2 st, vec2 center, float r1, float r2, float angle1, float angle2)
{
	st += 0.5 - center;
	vec2 p = st - 0.5;
	float z1 = atan(p.y, p.x);
	return (step(r1, dot(p, p) * 4.0) - step(r2, dot(p, p) * 4.0)) 
			* (step(angle1, atan(p.y, p.x)) - step(angle2, atan(p.y, p.x)));
}

float DrawRing(vec2 st, vec2 center, float r1, float r2)
{
	st += 0.5 - center;
	vec2 p = st - 0.5;
	return (step(r1, dot(p, p) * 4.0)) - (step(r2, dot(p, p) * 4.0));
}

float DrawRectangle(vec2 st, vec2 center, vec2 size)
{
	st += 0.5 - center;
	vec2 p = abs(st - 0.5);
	vec2 c =  1.0 - step(size, p * 2.0);
	return c.x * c.y;
}

float DrawRectangleWithRotate(vec2 st, vec2 center, vec2 size, float angle1, float angle2)
{
	// Rotating
	st -= center;
	st = Rotate2d(angle1) * st;
	st += center;

	st += 0.5 - center;

	// Shearing
	st.y -= size.y / 2.0;
	st.x += (st.y - 0.5) * tan(angle2);
	st.y += size.y / 2.0;

	vec2 p = abs(st - 0.5);
	vec2 c =  1.0 - step(size, p * 2.0);
	return c.x * c.y;
}

float DrawH(vec2 st, vec2 center)
{
	st += 0.5 - center;
	float f1 = DrawRectangle(st, vec2(0.3, 0.5), vec2(0.1, 0.5));
	float f2 = DrawRectangle(st, vec2(0.5, 0.5), vec2(0.5, 0.1));
	float f3 = DrawRectangle(st, vec2(0.7, 0.5), vec2(0.1, 0.5));
	return clamp(f1 + f2 + f3, 0.0, 1.0);
}

float DrawE(vec2 st, vec2 center)
{
	st += 0.5 - center;
	float f1 = DrawRectangle(st, vec2(0.5, 0.3), vec2(0.5, 0.1));
	float f2 = DrawRectangle(st, vec2(0.5, 0.5), vec2(0.5, 0.1));
	float f3 = DrawRectangle(st, vec2(0.5, 0.7), vec2(0.5, 0.1));
	float f4 = DrawRectangle(st, vec2(0.3, 0.5), vec2(0.1, 0.5));
	return clamp(f1 + f2 + f3 + f4, 0.0, 1.0);
}

float DrawL(vec2 st, vec2 center)
{
	st += 0.5 - center;
	float f1 = DrawRectangle(st, vec2(0.3, 0.5), vec2(0.1, 0.5));
	float f2 = DrawRectangle(st, vec2(0.5, 0.3), vec2(0.5, 0.1));
	return clamp(f1 + f2, 0.0, 1.0);
}

float DrawO(vec2 st, vec2 center)
{
	st += 0.5 - center;
	float f1 = DrawRing(st, center, 0.15, 0.35);
	return f1;
}

float DrawW(vec2 st, vec2 center)
{
	st += 0.5 - center;
	float f1 = DrawRectangleWithRotate(st, vec2(0.25, 0.5), vec2(0.5, 0.1), -PI / 2.5, -PI / 10.0);
	float f2 = DrawRectangleWithRotate(st, vec2(0.4, 0.5), vec2(0.5, 0.1), PI / 2.5, PI / 10.0);
	float f3 = DrawRectangleWithRotate(st, vec2(0.6, 0.5), vec2(0.5, 0.1), -PI / 2.5, -PI / 10.0);
	float f4 = DrawRectangleWithRotate(st, vec2(0.75, 0.5), vec2(0.5, 0.1), PI / 2.5, PI / 10.0);

	return clamp(f1 + f2 + f3 + f4, 0.0, 1.0);
}

float DrawR(vec2 st, vec2 center)
{
	st += 0.5 - center;
	float f1 = DrawRectangle(st, vec2(0.35, 0.5), vec2(0.1, 0.5));
	float f2 = DrawArc(st, vec2(0.55, 0.6), 0.01, 0.09, -PI / 2.0, PI / 2.0);
	float f3 = DrawRectangle(st, vec2(0.45, 0.7), vec2(0.2, 0.1));
	float f4 = DrawRectangle(st, vec2(0.45, 0.5), vec2(0.2, 0.1));
	float f5 = DrawRectangleWithRotate(st, vec2(0.6, 0.35), vec2(0.29, 0.1), -PI / 3.0, -PI / 6.0);

	return clamp(f1 + f2 + f3 + f4 + f5, 0.0, 1.0);
}

float DrawD(vec2 st, vec2 center)
{
 	st += 0.5 - center;
	float f1 = DrawArc(st, vec2(0.55, 0.5), 0.1, 0.25, -PI / 2.0, PI / 2.0);
	float f2 = DrawRectangle(st, vec2(0.35, 0.5), vec2(0.1, 0.5));
	float f3 = DrawRectangle(st, vec2(0.5, 0.7), vec2(0.2, 0.1));
	float f4 = DrawRectangle(st, vec2(0.5, 0.3), vec2(0.2, 0.1));

	return clamp(f1 + f2 + f3 + f4, 0.0, 1.0);
}

vec3 DrawAll(vec2 st)
{
	st *= vec2(5.0, 2.0);
	vec2 p = fract(st);

	float f1 = DrawH(p, vec2(0.5, 0.5)) * (step(0.0, st.x) - step(1.0, st.x)) * (step(1.0, st.y) - step(2.0, st.y));
	float f2 = DrawE(p, vec2(0.5, 0.5)) * (step(1.0, st.x) - step(2.0, st.x)) * (step(1.0, st.y) - step(2.0, st.y));
	float f3 = DrawL(p, vec2(0.5, 0.5)) * (step(2.0, st.x) - step(3.0, st.x)) * (step(1.0, st.y) - step(2.0, st.y));
	float f4 = DrawL(p, vec2(0.5, 0.5)) * (step(3.0, st.x) - step(4.0, st.x)) * (step(1.0, st.y) - step(2.0, st.y));
	float f5 = DrawO(p, vec2(0.5, 0.5)) * (step(4.0, st.x) - step(5.0, st.x)) * (step(1.0, st.y) - step(2.0, st.y));
	float f6 = DrawW(p, vec2(0.5, 0.5)) * (step(0.0, st.x) - step(1.0, st.x)) * (step(0.0, st.y) - step(1.0, st.y));
	float f7 = DrawO(p, vec2(0.5, 0.5)) * (step(1.0, st.x) - step(2.0, st.x)) * (step(0.0, st.y) - step(1.0, st.y));
	float f8 = DrawR(p, vec2(0.5, 0.5)) * (step(2.0, st.x) - step(3.0, st.x)) * (step(0.0, st.y) - step(1.0, st.y));
	float f9 = DrawL(p, vec2(0.5, 0.5)) * (step(3.0, st.x) - step(4.0, st.x)) * (step(0.0, st.y) - step(1.0, st.y));
	float f10 = DrawD(p, vec2(0.5, 0.5)) * (step(4.0, st.x) - step(5.0, st.x)) * (step(0.0, st.y) - step(1.0, st.y));

	vec3 bgColor = vec3(p, 1.0);
	vec3 fontColor = vec3(1.0, p);
	vec3 color;
	color = mix(bgColor, fontColor, f1);
	color = mix(color, fontColor, f2);
	color = mix(color, fontColor, f3);
	color = mix(color, fontColor, f4);
	color = mix(color, fontColor, f5);
	color = mix(color, fontColor, f6);
	color = mix(color, fontColor, f7);
	color = mix(color, fontColor, f8);
	color = mix(color, fontColor, f9);
	color = mix(color, fontColor, f10);

	return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = DrawAll(st);
    fragColor = vec4(color, 1.0);
}
