#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

void mainImage( out vec4 O, vec2 u )
{
    int s = 0;
    for (int i=0; i < 12; i++, u /= 2. )
        s +=  ( int(u.y)%2*2 + int(u)%2 ) << i+i;

    vec2 R = // iResolution.xy;
                exp2(ceil(log2(iResolution.xy))); // round to upper power of 2
    float v = float(s)/ R.x/R.y;                  // Z-curve index
    O = hue( 16.* v ) * sqrt(.1+1.7*v);           // display hue and luminance along index
  //O = fract(v*32.) * hue( v );
}
