void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float interpolation = (iTime -  float(int(iTime))); 

    bool diago_qui_monte = uv.x < uv.y;
    bool diago_qui_descend = uv.x + uv.y < 1.;

    bool au_dessus_de_y = uv.y > 0.5;
    bool a_droite_de_x = uv.x > 0.5;

    bool dans_le_cube = 
    (uv.x > 0.5 - sqrt(interpolation) * 0.5 && uv.x < 0.5 + sqrt(interpolation) * 0.5)
    && 
    (uv.y > 0.5 - sqrt(interpolation) * 0.5 && uv.y < 0.5 + sqrt(interpolation) * 0.5);
    bool chuiblanc = 
        (!diago_qui_descend && diago_qui_monte && !dans_le_cube && uv.x < 0.5)
        ||
        (diago_qui_descend && !diago_qui_monte && !dans_le_cube && uv.x > 0.5)
        ||
        (!diago_qui_descend && !diago_qui_monte && !dans_le_cube && uv.x > 0.5 && uv.y > 0.5)
        ||
        (diago_qui_descend && diago_qui_monte && !dans_le_cube && uv.x < 0.5 && uv.y < 0.5)
        ||
        (diago_qui_descend && !diago_qui_monte && dans_le_cube && uv.x < 0.5)
        ||
        (diago_qui_descend && diago_qui_monte && dans_le_cube && uv.x < 0.5 && uv.y > 0.5)
        ||
        (!diago_qui_descend && diago_qui_monte && dans_le_cube && uv.x > 0.5 && uv.y > 0.5)
        ||
        (!diago_qui_descend && !diago_qui_monte && dans_le_cube && uv.x > 0.5 && uv.y < 0.5)
        ;

    vec3 col = 0. + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)) * (chuiblanc ? 1. : 0.);
    vec3 col_nrv = 1. + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)) * (chuiblanc ? 0. : 1.);
    fragColor = vec4(vec3(chuiblanc ? col : col_nrv), 1.0);  

}
