float circle(vec2 uv, vec2 p, float r, float blur)
{
    uv -= p;
    float d = length(uv);
    return smoothstep(r, r - blur, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= vec2(0.5);
    uv.x *= iResolution.x/iResolution.y;

    vec3 color = vec3(.0);
    float mask = circle(uv, vec2(.0, .0), .4, .015);

    mask -= circle(uv, vec2(-.13, .2), .07, .015);
    mask -= circle(uv, vec2(.13, .2), .07, .015);

    float mouth = circle(uv, vec2(.0, -.0), .3, .015);
    mouth -= circle(uv, vec2(.0, 0.1), .3, .015);

    mask -= mouth;

    color = vec3(1., 1., 0.) * mask;
    fragColor = vec4(color, 1.);
}
