void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;


    vec2 uv1 = abs(uv-.5);
         uv1.y = pow(uv1.y,1.9);
         uv1.y +=iTime*.5;
         uv1.y = sin(uv1.y*10.)*.1;
   vec3 color = mix(vec3(1.,sin(iTime*2.),sin(iTime)),vec3(0.,sin(iTime),sin(iTime*1.5)),uv1.y*5.);

    vec3 col = vec3(step(abs((uv+uv1.y)-.5).x,.002));
    col = color*col;
  col +=col*5.;


    fragColor = vec4(col,1.0);
}
