void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    fragColor = texture(iChannel1,uv);

    // Output to screen
    fragColor += texture(iChannel0,uv);
}

float OPACITY = 0.7;
float PARTICLE_SIZE = 16.0;
float SKEW = 0.5;  // 0.0 to 1.0, left to right


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.y;
    vec2 uv2 = fragCoord/iResolution.xy;

    vec2 randDir = normalize(
        texture(iChannel1, uv2 * 0.4 + sin(iTime * 0.2) * 0.2).xy - SKEW + vec2(0., -1.)
    );

    vec4 old = texture(iChannel0, uv2 + randDir * 0.4 * iTimeDelta);

    //vec2 pos = iResolution.xy/2.0;
    vec2 pos = vec2(iResolution.x/2., 40.);
    float particleDist = distance(fragCoord.xy, pos);

    vec2 ff = vec2(0.5, 0.5)*vec2(iResolution.x/iResolution.y, 1.);
    float intensity = length(ff - uv);
    intensity = smoothstep(OPACITY, 0., intensity);

    if (particleDist < PARTICLE_SIZE) { fragColor.rgb += intensity; };

    fragColor = mix(old, fragColor, fragColor);
}
