// Lamé curve
// (|x/a|^n) + (|y/b|^n) = 1

float n = 3.00; // Lamé curve n
float a = 1.00; // Lamé curve a
float b = 1.00; // Lamé curve b
float s = 0.02; // smooth

vec4 color = vec4(255./255., 103./255., 0.0, 1.0);

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.yy;
    uv = uv - vec2(iResolution.x / iResolution.y / 2.0, 0.5);
    uv = uv * 3.0;

    float sum = pow(abs(uv.x / a), n) + pow(abs(uv.y), n);
    float c = smoothstep(1.0, 1.0 - s, sum);

    fragColor = vec4(c, c, c, 1.0) * color;
}
