vec3 rand_dir;


// RENDER CONSTANTS
const float INF = 1e10;
const int N_STEPS = 10;
const float NOISE_AMP = 0.06;
const float NOISE_FREQ = 4.0;
const float TABLE_RAD = 30.0;
const int MARCH_STEPS = 3;
const int NOISE_STEPS = 10;


// ENV CONSTANTS
const int EMISSION = 0;
const int DIFFUSION = 1;
const int REFLECTION = 2;
const int REFRACTION = 3;

const float GLASS_COEFF = 1.5;
const float AIR_COEFF = 1.0;


// SCENE CONSTANTS
const vec3 CAMERA_ORIENTATION =  vec3(3, 2, 7);
const int N_LIGHT_SOURCES = 2;
const vec3 FIREBALL_ANIM = vec3(0.0, -3.0, 1.5);


// SCENE MODELS
struct Sphere {
    vec3 pos;
    vec3 color;
    float radius;
    float atten;
};

struct Cube {
    vec3 p[3];
};

struct Square {
    vec3 v[4];
};


// SCENE OBJECTS
Cube cube = Cube(vec3[3](
    vec3(-1.0, -0.99, -1.0),
    vec3(1.0, -0.99, -1.0),
    vec3(-1.0, -0.99, 1.0)
));

Sphere Fireball = Sphere(vec3(0.0), vec3(0.0), 0.5, 0.0);

Sphere LIGHT_SOURCES[N_LIGHT_SOURCES] = Sphere[N_LIGHT_SOURCES](
    Sphere(vec3(2, 1.5, 0), vec3(0.0, 0.5, 1), 0.5, 20.0),
    Sphere(vec3(-4, 1.5, -0.2), vec3(0.8, 0.0, 1.0), 0.35, 15.0)
);


// TRACING
float trace_plane(vec3 pos, vec3 dir, out vec3 normal) {
    float t = (-1.5 - pos.y) / dir.y;
    if (t <= 0.0) {
        return INF;
    }

    vec3 world_pos = t * dir + pos;
    if (dot(world_pos.xz, world_pos.xz) >= TABLE_RAD) {
        return INF;
    }
    normal = vec3(0, 1, 0);
    return t;
}

float trace_sphere(vec3 pos, vec3 dir, float r, out vec3 normal) {
    float b = dot(pos, dir);
    float discr = b * b - dot(pos, pos) + r * r;
    if (discr < 0.0) {
        return INF;
    }
    float t = -b - sqrt(discr);
    if (t > 0.0) {
        normal = normalize(pos + t * dir);
        return t;
    }
    t = -b + sqrt(discr);
    if (t < 0.0) {
        return INF;
    }
    normal = normalize(pos + t * dir);
    return t;
}

float trace_side(vec3 pos, vec3 dir, vec3 p0, vec3 p1, vec3 p2, out vec3 normal) {
    normal = normalize(cross(p2 - p0, p1 - p0));
    float d = dot(normal, dir);
    if (abs(d) <  0.0001f) {
        return INF;
    }
    float t = dot(p0 - pos, normal) / d;
    return t < 0.0 ? INF : t;
}

#define orient(a, b, c, normal) dot(cross(b - a, c - a), normal)

float trace_square(vec3 pos, vec3 dir, Square s, out vec3 normal) {
    float t = trace_side(pos, dir, s.v[0], s.v[1], s.v[3], normal);
    if (t == INF) {
        return INF;
    }
    vec3 q = pos + dir * t;
    float o1 = orient(q, s.v[0], s.v[1], normal);
    float o2 = orient(q, s.v[1], s.v[2], normal);
    float o3 = orient(q, s.v[2], s.v[3], normal);
    float o4 = orient(q, s.v[3], s.v[0], normal);
    if (o1 * o2 > 0.0 && o2 * o3 > 0.0 && o3 * o4 > 0.0) {
        return t;
    }
    return INF;
}

float trace_cube(vec3 pos, vec3 dir, Cube cube, out vec3 normal) {
    vec3 p0 = cube.p[0];
    vec3 p1 = cube.p[1];
    vec3 p2 = cube.p[2];
    vec3 a = p2 - p0;
    vec3 b = p1 - p0;
    vec3 c = normalize(cross(a, b)) * length(a);
    vec3 p3 = p2 + b;
    vec3 p4 = p0 + c;
    vec3 p5 = p4 + b;
    vec3 p6 = p4 + a;
    vec3 p7 = p6 + b;
    Square s1 = Square(vec3[4](p0, p1, p3, p2));
    Square s2 = Square(vec3[4](p0, p4, p6, p2));
    Square s3 = Square(vec3[4](p3, p6, p7, p3));
    Square s4 = Square(vec3[4](p3, p7, p5, p1));
    Square s5 = Square(vec3[4](p1, p5, p4, p0));
    Square s6 = Square(vec3[4](p4, p5, p7, p6));
    Square s[6] = Square[6](s1, s2, s3, s4, s5, s6);
    float t = INF;
    vec3 tmp_normal;
    for (int i = 0; i < s.length(); i++) {
        float tt = trace_square(pos, dir, s[i], tmp_normal);
        if ( tt < t) {
            t = tt;
            normal = tmp_normal;
        }
    }
    return t;
}

float trace_cylinder(vec3 pos, vec3 dir, out vec3 normal) {
    float t = (-1.0 - pos.y) / dir.y;
    if (t <= 0.0) {
        return INF;
    }
    vec3 worldPos = t * dir + pos;
    if (dot(worldPos.xz, worldPos.xz) < 1.0) {
        normal = vec3(0, 1, 0);
        return t;
    }

    // dot(pos + t * dir, pos + t * dir) == r * r;
    // dot(pos, pos) + 2 * t * dot(pos, dir) + t * t * dot(dir, dir) == r * r
    // t * t + 2.0 * t * dot(pos, dir) + dot(pos, pos) - r * r == 0
    float a = dot(dir.xz, dir.xz);
    float b = dot(pos.xz, dir.xz);
    float c = dot(pos.xz, pos.xz) - 1.0;
    float D = b * b - a * c;
    if (D < 0.0) {
        return INF;
    }
    t = (-b - sqrt(D)) / a;
    if (t > 0.0) {
        worldPos = t * dir + pos;
        if (worldPos.y <= -1.0) {
            normal = normalize(vec3(worldPos.x, 0, worldPos.z));
            return t;
        }
    }
    t = (-b + sqrt(D)) / a;
    if (t < 0.0) {
        return INF;
    }
    worldPos = t * dir + pos;
    if (worldPos.y <= -1.0) {
        normal = normalize(vec3(worldPos.x, 0, worldPos.z));
        return t;
    }
    return INF;
}


bool is_occluded(vec3 pos, vec3 target) {
    vec3 dir = target - pos;
    float dist = length(dir);
    dir /= dist;
    vec3 cube_normal;
    float cubeT = trace_cube(pos, dir, cube, cube_normal);
    if (cubeT < dist) {
        return true;
    }
    vec3 cyl_normal;
    float cylT = trace_cylinder(pos, dir, cyl_normal);
    if (cylT < dist) {
        return true;
    }
    return false;
}

vec3 compute_lightning(vec3 pos, vec3 color, vec3 normal) {
    vec3 res;
    for (int i = 0; i < N_LIGHT_SOURCES; i++) {
        vec3 toLight = LIGHT_SOURCES[i].pos - pos;
        float distSqr = dot(toLight, toLight);
        float atten = is_occluded(pos, LIGHT_SOURCES[i].pos + rand_dir * LIGHT_SOURCES[i].radius) ? 0.0 : LIGHT_SOURCES[i].atten / distSqr;
        res += max(0.0, dot(normal, normalize(toLight))) * atten * LIGHT_SOURCES[i].color;

    }
    return color * (res + texture(iChannel1, normal).rgb * 0.5);
}

vec3 refraction(vec3 v, vec3 normal, float n1, float n2) {
    if (dot(v, normal) < 0.0) {
        normal = -normal;
    }
    float cosA = dot(v, normal);
    float sinA = sqrt(1.0 - cosA * cosA);
    vec3 tang = normalize(v - cosA * normal);
    float sinB = sinA / n2 * n1;
    float cosB = sqrt(1.0 - sinB * sinB);
    return sinB * tang + cosB * normal;
}


# define mod289(x) (x - floor(x * (1.0 / 100.0)) * 100.0)



vec4 permute(vec4 x) {
    return mod289(((x*34.0)+1.0)*x);
}

vec4 taylorInvSqrt(vec4 r) {
    return 1.79284291400159 - 0.85373472095314 * r;
}




float snoise(vec3 v)
{
	const vec2  C = vec2(1.0/6.0, 1.0/3.0);
	const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

	vec3 i  = floor(v + dot(v, C.yyy));
	vec3 x0 = v - i + dot(i, C.xxx);

	vec3 g = step(x0.yzx, x0.xyz);
	vec3 l = 1.0 - g;
	vec3 i1 = min(g.xyz, l.zxy);
	vec3 i2 = max(g.xyz, l.zxy);
	vec3 x1 = x0 - i1 + C.xxx;
	vec3 x2 = x0 - i2 + C.yyy;
	vec3 x3 = x0 - D.yyy;

	i = mod289(i);
	vec4 p = permute( permute( permute( i.z + vec4(0.0, i1.z, i2.z, 1.0)) + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

	float n_ = 0.142857142857;
	vec3  ns = n_ * D.wyz - D.xzx;
	vec4 j = p - 49.0 * floor(p * ns.z * ns.z);
	vec4 x_ = floor(j * ns.z);
	vec4 y_ = floor(j - 7.0 * x_);
	vec4 x = x_ *ns.x + ns.yyyy;
	vec4 y = y_ *ns.x + ns.yyyy;
	vec4 h = 1.0 - abs(x) - abs(y);
	vec4 b0 = vec4(x.xy, y.xy);
	vec4 b1 = vec4(x.zw, y.zw);
	vec4 s0 = floor(b0) * 2.0 + 1.0;
	vec4 s1 = floor(b1) * 2.0 + 1.0;
	vec4 sh = -step(h, vec4(0.0));
	vec4 a0 = b0.xzyw + s0.xzyw * sh.xxyy;
	vec4 a1 = b1.xzyw + s1.xzyw * sh.zzww;
	vec3 p0 = vec3(a0.xy, h.x);
	vec3 p1 = vec3(a0.zw, h.y);
	vec3 p2 = vec3(a1.xy, h.z);
	vec3 p3 = vec3(a1.zw, h.w);

	vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
	p0 *= norm.x;
	p1 *= norm.y;
	p2 *= norm.z;
	p3 *= norm.w;

	vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
	m = m * m;
	return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), dot(p2,x2), dot(p3,x3)));
}

float turbulence(vec3 position, float minFreq, float maxFreq, float qWidth)
{
	float value = 0.0;
	float cutoff = clamp(0.5/qWidth, 0.0, maxFreq);
	float fade;
	float fOut = minFreq;
	for(int i=NOISE_STEPS ; i>=0 ; i--)
	{
		if(fOut >= 0.5 * cutoff) break;
		fOut *= 2.0;
		value += abs(snoise(position * fOut))/fOut;
	}
	fade = clamp(2.0 * (cutoff-fOut)/cutoff, 0.0, 1.0);
	value += fade * abs(snoise(position * fOut))/fOut;
	return 3.0-value;
}

float sphere_dist(vec3 position, float r) {
	return length(position - Fireball.pos) - r;
}


#define Color1 vec3(1.0, 1.0, 1.0)
#define Color2 vec3(1.0, 0.5, 0.2)
#define Color3 vec3(1.0, 0.03, 0.0)
#define Color4 vec3(0.1, 0.02, 0.0)

#define saturate(a) clamp(a, 0.0, 1.0)

vec3 shade(float distance) {
	float c1 = saturate(distance*5.0 + 0.5);
	float c2 = saturate(distance*5.0);
	float c3 = saturate(distance*3.4 - 0.5);

	vec3 a = mix(Color1,Color2, c1);
	vec3 b = mix(a,     Color3, c2);
	return 	 mix(b,     Color4, c3);
}

float render_ball(vec3 position, out float distance) {
	float noise = turbulence(position * NOISE_FREQ + FIREBALL_ANIM * 1.0, 0.1, 1.5, 0.03) * NOISE_AMP;
	noise = saturate(abs(noise));

	distance = sphere_dist(position, Fireball.radius) - noise;
	return noise;
}

vec3 ray_march(vec3 ray_origin, vec3 ray_step) {
	vec3 position = ray_origin;
	float distance;
	float displacement;
	for(int step = 0; step < MARCH_STEPS; step++)
	{
		displacement = render_ball(position, distance);
		if(distance < 0.05) {
            break;
        }
		position += ray_step * distance;
	}
	return mix(shade(displacement), vec3(0, 0, 0), float(distance >= 0.5));
}

bool intersect_sphere(vec3 ro, vec3 rd, vec3 pos, float radius, out vec3 i_point) {
	vec3 rel_distance = (ro - pos);
	float b = dot(rel_distance, rd);
	float c = dot(rel_distance, rel_distance) - radius * radius;
	float d = b*b - c;
	i_point = ro + rd*(-b - sqrt(d));
	return d >= 0.0;
}

vec3 get_fireball_col(vec2 p) {
	float rotx = 0.6;
	float roty = 0.8;
	float zoom = 10.0;
	vec3 ro = zoom * normalize(vec3(cos(roty), cos(rotx), sin(roty)));
	vec3 ww = normalize(vec3(0.0, 0.0, 0.0) - ro);
	vec3 uu = normalize(cross( vec3(0.0, 1.0, 0.0), ww));
	vec3 vv = normalize(cross(ww, uu));
	vec3 rd = normalize(p.x*uu + p.y*vv + 1.5*ww);

	vec3 col;
	vec3 origin;

	if(intersect_sphere(ro, rd, Fireball.pos, Fireball.radius + NOISE_AMP*12.0, origin))
	{
		col = ray_march(origin, rd);
	}

    return vec3(0, col.g, col.b);
}

float burn_snoise(vec3 uv, float res)
{
	const vec3 s = vec3(1e0, 1e2, 1e3);
	uv *= res;
	vec3 uv0 = floor(mod(uv, res))*s;
	vec3 uv1 = floor(mod(uv+vec3(1.), res))*s;
	vec3 f = fract(uv);
	f = f*f*(3.0-2.0*f);
	vec4 v = vec4(uv0.x+uv0.y+uv0.z, uv1.x+uv0.y+uv0.z,
	              uv0.x+uv1.y+uv0.z, uv1.x+uv1.y+uv0.z);
	vec4 r = fract(sin(v*1e-1)*1e3);
	float r0 = mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y);
	r = fract(sin((v + uv1.z - uv0.z)*1e-1)*1e3);
	float r1 = mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y);
	return mix(r0, r1, f.z) * 2.0 - 1.0;
}

vec3 burn (vec2 p)
{
	float color1 = 4.0 - (2.0 * length(2.5 * p));
	vec3 coord = vec3(atan(p.x, p.y) / 6.2832 + 0.5, length(p) * 0.4, 0.5);
	for(int i = 1; i <= 3; i++)
	{
		float power = pow(2.0, float(i));
		color1 += 0.5*(1.5 / power) * burn_snoise(coord + vec3(0.0, -0.05, -0.01), power * 16.0);
	}
	color1 *= 0.5;

    return vec3( color1, pow(max(color1, 0.0), 2.0)*0.4, pow(max(color1, 0.0), 3.0) * 0.05);
    return vec3( 0, 0, pow(max(color1, 0.0), 3.0) * 0.15);
}

float pow2(float x) {
    return x * x;
}

float get_random(float frame) {
        return fract(sin(dot(vec3(frame), vec3(12.9898,78.233,45.5432))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 rand_vals = vec3(get_random(float(iFrame)), get_random(float(iFrame + 5)), get_random(float(iFrame + 15)));
    rand_dir = normalize(rand_vals - 0.5);
    vec2 uv = (fragCoord - iResolution.xy * 0.5 + (rand_vals.xy - 0.5) * 2.0) / iResolution.x;
    vec3 front_vec = normalize(vec3(-CAMERA_ORIENTATION));
    vec3 up_vec = vec3(0, 1, 0);
    vec3 right_vec = normalize(cross(front_vec, up_vec));
    up_vec = normalize(cross(right_vec, front_vec));
    vec3 view_vec = normalize(front_vec + right_vec * uv.x + up_vec * uv.y);
    vec3 cur_pos = CAMERA_ORIENTATION;
    vec3 cur_dir = view_vec;
    float GLASS_R = pow2(AIR_COEFF - GLASS_COEFF) / pow2(AIR_COEFF + GLASS_COEFF);
    vec4 col = vec4(0, 0, 0, 0);

    float n1 = AIR_COEFF;
    float n2 = GLASS_COEFF;

    vec3 color_mult = vec3(1, 1, 1);

    for (int i = 0; i < N_STEPS; i++) {
        float t = INF;
        int material = EMISSION;
        vec3 color, normal, plane_normal;

        float planeT = trace_plane(cur_pos, cur_dir, plane_normal);
        if (planeT < t) {
            t = planeT;
            material = DIFFUSION;
            vec3 world_pos = t * cur_dir + cur_pos;
            color = texture(iChannel3, world_pos.xz * 0.2).rgb;
            normal = plane_normal;

        }

        for (int j = 0; j < N_LIGHT_SOURCES; j++) {
            vec3 light_normal;
            float lightT = trace_sphere(cur_pos - LIGHT_SOURCES[i].pos, cur_dir, LIGHT_SOURCES[i].radius, light_normal);
            if (lightT < t) {
                t = lightT;
                material = EMISSION;
                color = LIGHT_SOURCES[i].color;
                normal = light_normal;
            }
        }

        vec3 cyl_normal;
        float cylT = trace_cylinder(cur_pos, cur_dir, cyl_normal);
        if (cylT < t) {
            vec3 world_pos = t * cur_dir + cur_pos;
            t = cylT;
            material = DIFFUSION;
            color = texture(iChannel0, world_pos.xz * world_pos.y).rgb;
            normal = cyl_normal;
        }

        vec3 cube_normal;
        float cubeT = trace_cube(cur_pos, cur_dir, cube, cube_normal);
        if (cubeT < t) {
            t = cubeT;
            normal = cube_normal;
            if (rand_vals.x < GLASS_R) {
                material = REFLECTION;
            } else {
                color_mult *= vec3(0.2, 0.3, 1.0);
                material = REFRACTION;
                vec2 p = (fragCoord.xy / iResolution.xy) - 0.5;
                p.x *= iResolution.x / iResolution.y;
                vec3 fb = get_fireball_col(p);
                vec3 fo = burn(p);
                col += vec4(mix(fb, fo * vec3(0.7, 0.5, 0.5), 0.5), 0.8);
            }
        }

        if (t != INF) {
            vec3 world_pos = t * cur_dir + cur_pos;
            col += vec4(texture(iChannel0, world_pos.xz).rgb * 0.1, 0.0);
            if (material == EMISSION) {
                col += vec4(color * color_mult * 0.5, 0);
                break;
            } else if (material == DIFFUSION) {
                col += vec4(compute_lightning(world_pos, color, normal) * color_mult, 0);
                break;
            } else if (material == REFLECTION) {
                cur_dir = reflect(cur_dir, normal);
                cur_pos = world_pos + cur_dir * 1e-3;
            } else if (material == REFRACTION) {
                cur_dir = refraction(cur_dir, normal, n1, n2);
                cur_pos = world_pos + cur_dir * 1e-3;
            }
        } else {
            col += vec4(texture(iChannel1, cur_dir).rgb * color_mult * 0.2, 0);
        }
    }
    fragColor = vec4(col);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv) + texture(iChannel1, uv);
}
