void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    float dis = abs(uv.x - 0.5);
    float r = 0.3 * (1.2 - uv.y);

    //fragColor = texture(iChannel0, uv); 
    fragColor = vec4(0.2); 
    float darkness = abs(mod(iTime, 2.0) - 1.0); 

    vec3 color = vec3(0.4, 0.35, 0.18);
    if(dis < r)
    {
       float circle = smoothstep(r, r * 0.05, dis);
       float alpha = clamp(1.8 * uv.y - 0.25, 0.0, 1.6); 
       vec3 newColor = color * circle * alpha;
       newColor = 1.0 - (1.0 - fragColor.rgb) * (1.0 - newColor);
       fragColor.rgb = mix(newColor.rgb, fragColor.rgb, darkness);
    }

    r = r * 0.5;
    color = vec3(0.4, 0.35, 0.18);
    if(dis < r)
    {
       float circle = smoothstep(r, r * 0.05, dis);
       float alpha = clamp(1.8 * uv.y - 0.25, 0.0, 1.6); 
       vec3 newColor = color * circle * alpha;
       newColor = 1.0 - (1.0 - fragColor.rgb) * (1.0 - newColor);
       fragColor.rgb = mix(newColor.rgb, fragColor.rgb, darkness);
    }

    r = r * 0.5;
    color = vec3(0.2, 0.18, 0.09);
    if(dis < r)
    {
       float circle = smoothstep(r, r * 0.05, dis);
       float alpha = clamp(1.8 * uv.y - 0.25, 0.0, 1.6); 
       vec3 newColor = color * circle * alpha;
       newColor = 1.0 - (1.0 - fragColor.rgb) * (1.0 - newColor);
       fragColor.rgb = mix(newColor.rgb, fragColor.rgb, darkness);
    }



    vec4 color2 = texture(iChannel1, uv); 
    fragColor.rgb = mix(color2.rgb, fragColor.rgb, 0.8);
}
