// 
// This shader is generated by my new web app: dot2shader
//
// https://iwbtshyguy.gitlab.io/dot2shader/
//
// This web app generates a shader for Shadertoy from a pixel art by only uploading an image file.
//

const vec3 PALETTE[] = vec3[](
    vec3(0, 0, 0) / 255.0,
    vec3(252, 0, 0) / 255.0,
    vec3(252, 127, 127) / 255.0
);

const int WIDTH = 13, HEIGHT = 11, CHUNKS_IN_U32 = 16;
const int BUFFER[] = int[](
    4096, 1342177616, 1431306261, 357912577, 1364546896, 1498764629, 1437160789, 1427476821,
    22025473
);

vec3 getColor(in ivec2 u) {
    int idx = u.y * WIDTH + u.x;
    u = ivec2(idx % CHUNKS_IN_U32, idx / CHUNKS_IN_U32);
    int bitShift = 32 / CHUNKS_IN_U32;
    return PALETTE[BUFFER[u.y] >> u.x * 32 / CHUNKS_IN_U32 & (1 << bitShift) - 1];
}

void mainImage(out vec4 O, in vec2 U) {
    vec2 r = iResolution.xy;
    ivec2 u = ivec2(floor((U - 0.5 * r) / r.y * float(HEIGHT) + vec2(WIDTH, HEIGHT) / 2.0));
    O.xyz = u == abs(u) && u.x < WIDTH && u.y < HEIGHT ? getColor(u) : vec3(0.5);
}
