precision highp float;

const float PI = 3.14;
const float e = 2.71;

const float R = 0.5;
const vec2 eyePoint = vec2(.7, 0.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;
    vec3 eyeA = vec3(1.);
    vec3 eyeB = vec3(1.);
    float halo = abs(sin(iTime)) * 0.3 + 0.1;

    for(int i = 0; i < 5; i++) {
        float angle = float(i + 1) * PI/5.;
        vec2 pointC = vec2(R * cos(angle), R * sin(angle)) + eyePoint;
        float r = distance(uv, pointC);

        float dec = pow(e, (-r + 0.4)/halo);
        eyeA *= vec3(dec, 0., 0.);
    }

    for(int i = 0; i < 5; i++) {
        float angle = float(i + 1) * PI/5.;
        vec2 pointC = vec2(R * cos(angle), R * sin(angle)) + eyePoint;
        float r = distance(vec2(-uv.x, uv.y), pointC);

        float dec = pow(e, (-r + 0.4)/halo);
        eyeB *= vec3(0., 0., dec);
    }

    fragColor = vec4(eyeA, 1.) + vec4(eyeB, 1.);
}
