// utils from https://www.shadertoy.com/view/llySRh
vec4 char(vec2 p, int c) {
    vec2 dFdx = dFdx(p/16.), dFdy = dFdy(p/16.);
    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1e5);
	return textureGrad( iChannel3, p/16. + fract( vec2(c, 15-c/16) / 16. ), 
                        dFdx, dFdy );
}
vec4 pInt(vec2 p, float n) {
    vec4 v = vec4(0);
    for (int i = int(n); i>0; i/=10, p.x += .5 )
        v += char(p, 48+ i%10 );
    return v;
}


void mainImage(out vec4 O,  vec2 U )
{
	vec2  R = iResolution.xy, p = 2.*U-R;
    float t = iMouse.z > 0. ? iTime : 0.,                       // click for motion
          a = 100.* ( atan(p.y,p.x) +0.*length(p)/R.y + t ),    // rays pattern
          v = mix( .5+.5*cos(a)* smoothstep(6.14,0.,fwidth(a)), // display rays
                   pInt( p/360.-vec2(1,-.5), 3452839. ).x,      // display digits
                   .05 );
    O = vec4( pow( v, 1./2.2 ) );                               // to sRGB
}
