// 99.9% from https://www.shadertoy.com/view/ssdfDS



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y + vec2(-0.25,0);

    vec2 mouse = 2.0 * ((iMouse.xy / iResolution.y) - 0.5);
    if (length(iMouse) == 0.0) {
      mouse = vec2(cos(iTime), sin(iTime));
    }

    float ITERATONS = 1000.0;
    float THRESHOLD = 32.0;

    //Normalization
    float offset_x=0.7;
    float offset_y=0.0;
    float scale = 1./pow(float(iFrame)/10., 3.);
    scale = 1.0;
    //scale = 1./scale;
    float a=-scale-offset_x,b=scale-offset_x,c=-scale-offset_y,d=scale-offset_y;
    uv = vec2(uv.x*abs(a-b)+a, uv.y*abs(c-d)+c);

    vec2 z = mouse;
    float age = 0.0;
    bool is_bounded = true;
    for(int i=0; i<int(ITERATONS); i++){
        if(z.x*z.x + z.y*z.y > THRESHOLD){
            is_bounded = false;
            break;
        }
        z = vec2((z.x*z.x)-(z.y*z.y), 2.0*z.x*z.y) + uv;
        age++;
    }

    // Time varying pixel color
    vec3 col = vec3(0.0);
    if(!is_bounded){
        col = vec3(pow(age/ITERATONS*10., 0.75));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
