const float PI = 3.14,
    d = 0.005,
    R = 1.;

vec2 rotZ(vec2 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c) * p;
}

vec4 draw(vec2 U, float a, float b) {
    vec2 rotU = rotZ(U, a);
    vec4 c = vec4(min(exp((-abs(rotU.x) + rotU.y/b)/d), 1.));
    return vec4(c);
}

// from https://www.shadertoy.com/view/4djSRW
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

void mainImage( out vec4 O, in vec2 u ) {
    vec2 Res = iResolution.xy, 
        U = (2. * u - Res) / Res.y;
    O = vec4(0.);

    for(float i = 1.; i <= 12.; i++) {
        float a = hash11(i);
        float b = 2. * PI * a;
        float c = PI * a;
        vec2 d = R * vec2(sin(b), cos(b));
        vec4 C = length(U - d) * vec4(sin(c), sin(PI * hash11(i * i)), sin(PI * hash11(i * i * i)), 1.);
        O += 0.2 * C * a * draw(U - d, b * PI + iTime * a, 0.5 * a);
    }
}
