void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float w = fwidth(uv.x);


    uv.y += sin(uv.x*5. + iTime)*0.3-0.5;


    float top = smoothstep(0.5*sin(uv.x*30.), 0., sin(uv.y));


    vec3 col = vec3(top);

    col*= smoothstep(0.5*sin(uv.x*60.), 0., vec3(0., 1., 1.));


    // Output to screen
    fragColor = vec4(col,1.0);
}
