float P = acos(-1.);
float B(vec2 p,float n)
{
    float r=n*2.5/P;vec2 m=abs(mat2(cos(r),sin(r),-sin(r),cos(r))*p)-vec2(.1,.35);
    return step(max(m.x,m.y),.01);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 f=fract((fragCoord.xy*2.-iResolution.xy)/iResolution.y*2.)-.5;
	vec3 c;
    c+=B(vec2(f.x,f.y-.5),-1.)+B(vec2(f.x,f.y+.5),-1.)+B(vec2(f.x-.5,f.y),1.)+B(vec2(f.x+.5,f.y),1.);
	fragColor=vec4(c,1.);
}
