void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float speed = 0.05;
    float size = 0.045;
    float alphaCut = 0.4;
    float timeOffset = 106.70;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x = uv.x > 0.5 ? 1.0 - uv.x : uv.x;

    float circleMask = 1.0 - length((uv - vec2(0.5, 0.5))*2.0);
    float tex1 = texture(iChannel0, (uv + (iTime + timeOffset) * speed) * size).r;
    float tex2 = texture(iChannel0, (uv - (iTime + timeOffset) * speed) * size).r;
    float noise = tex1 * tex2;

    float col = mix(1.0, noise, circleMask);
    col = step(alphaCut, col);

    // Output to screen
    fragColor = vec4(col, col, col, 1.0);
}
