// Left, incorrect SDF to a Squircle. Right, correct SDF but brute-force and
// resolution dependent SDF, so it's not very useful in practice. This is NOT
// a great way go blend between a circle and a square btw; for that you can
// use https://www.shadertoy.com/view/7sdXz2

// Gradient here: https://www.shadertoy.com/view/fs3yDM


float sdSquircle(vec2 p, float n)
{
    // symmetries
    p = abs(p); if( p.y>p.x ) p=p.yx;

    n = 2.0/n; // note the remapping in order to match the
               // implicit and the explicit versions

    const int num = 16; // tesselate into 8x16=128 segments, more denselly at the corners
    float s = 1.0;
    float d = 1e20;
    vec2 oq = vec2(1.0,0.0);
    for( int i=1; i<=num; i++ )
    {
        float h = float(i)/float(num);
        // if( n<2.0 ) h = pow( h, 1.0/n );  else h = pow( h, 2.0/n ); // uniform distribution
        float an = (6.283185/8.0)*h;
        vec2  q = pow(vec2(cos(an),sin(an)),vec2(n));
        vec2  pa = p-oq;
        vec2  ba = q-oq;
        vec2  z = pa - ba*clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
        float d2 = dot(z,z);
        if( d2<d )
        {
            d = d2;
            s = pa.x*ba.y - pa.y*ba.x;
        }
        oq = q;
    }
    return sqrt(d)*sign(s);
}

float incorrect_sdSquircle(vec2 p, float n)
{
    p = abs(p);
    float w = pow(p.x,n) + pow(p.y,n);

    // implicit
    // return pow(w,1.0/n) - 1.0;

    // linearlized implicit (taylor)
    float b = 2.0*n - 2.0;
    float a = -1.0/n;
    return w*(1.0-pow(w,a)) / sqrt(pow(p.x,b)+pow(p.y,b));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    p *= 1.4;
    m *= 1.4;

     // animation
    float n = 3.0 + 2.5*sin(6.283185*iTime/3.0);

    // distance
    float d = (p.y<p.x) ? sdSquircle(p, n) : 
                          incorrect_sdSquircle( p, n );
    // coloring
    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.65,0.85,1.0);
	col *= 1.0 - exp(-8.0*abs(d));
    col *= 0.6 + 0.4*smoothstep(-0.5,0.5,cos(90.0*abs(d)));

	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

    // mouse interaction
    if( iMouse.z>0.001 )
    {
    d = sdSquircle(m,n);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

    col *= smoothstep( 0.005, 0.010, abs(p.y-p.x) );

	fragColor = vec4(col, 1.0);
}
