void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)


    float width = max(sin(iTime *  float(10)) , 0.5) + float(0.4);
    // Time varying pixel color
    vec3 col = vec3(width,width,width) / abs((iMouse.y - fragCoord.y) + sin(iTime * vec3(15,15,15))*sin((iMouse.x - fragCoord.x) * vec3(1,0.1,1)));

    col +=  vec3(width,width,width) / abs((iMouse.x - fragCoord.x) + sin(iTime * vec3(15,15,15))*sin((iMouse.y - fragCoord.y) * vec3(1,0.1,0.1)));



    //col += (vec3(10,10,10)/(iMouse.x - fragCoord.x)*(iMouse.x - fragCoord.x) + (iMouse.y - fragCoord.y)*(iMouse.y - fragCoord.y)-vec3(1,1,1));

    // Output to screen
    fragColor = vec4(col,1.0);
}
