// variant of https://shadertoy.com/view/7d3yWS
// variant of https://shadertoy.com/view/fddyRs
// using marching triangles https://www.shadertoy.com/view/fdccDB
 int lod = 5;
 float thresh = .5;

#define T(U)  textureLod(iChannel0, U*R.y/R , float(lod-delta) ).r // or lod=0 for fun
#define G(uv) ( ( (floor(U) + uv) *mat2(1,.5,0,.86) ) *Z/1024. )
#define S(v) smoothstep( -.8,.8,(iMouse.z>0.?abs(v):v)/fwidth(v) ) // antialiasing draw

float vector(vec2 U, int delta)
{
    float Z = exp2(float(lod));                           // scaling
    vec2  v, R = iResolution.xy;
    float O = 0.;                                         // --- marching triangle -------
    U *= mat2(1,-1./1.73, 0,2./1.73) *1024./Z/R.y;        // conversion to
    vec3 g = vec3(U, 1.-U.x-U.y);                         // hexagonal coordinates
	g = fract(g);                                         // diamond coords
    bool side = length(g)>1.;                             // tri: which diamond half

    vec3 V = side ? vec3( T(G(vec2(1))), T(G(vec2(0,1))), T(G(vec2(1,0))) ) // samples at corder
                  : vec3( T(G(vec2(0))), T(G(vec2(1,0))), T(G(vec2(0,1))) );
    if (side) g = 1.-g;                                   // barycentric coordinates
    O = S( (1.-g.x-g.y)*V.x + g.x*V.y + g.y*V.z - thresh );
    return O;
}

void mainImage( out vec4 O, vec2 U )
{   
    O = vec4( vector(U,3) + vector(U,2) + vector(U,1) + vector(U,0)  + vector(U,-1) ) / 5.;
}
