// "The china Flag" 
// by huataishi 

vec3 WHITE = vec3(1);
vec3 RED = vec3(255, 0, 0)/255.;
vec3 YELLOW = vec3(255, 255, 0)/255.;
float BIGSTARTSIZE = 0.051;
float ROTATO = 18.;

float Star(vec2 uv, float size,vec2 pos,float rotato) {
   uv.x *= iResolution.x / iResolution.y;
   uv -= pos;
   uv *= mat2(cos(3.14*rotato/180.),-sin(3.14*rotato/180.),sin(3.14*rotato/180.),cos(3.14*rotato/180.));
   float a = mod( atan(uv.x,uv.y)*5.,6.28 ) / 5. -3.14/5.,
      angle = 3.14*(.5+.2),
          d = length(uv)*  sin(angle+abs(a)),
          w = fwidth(d);
    return length(uv)>.8 ? 0. : smoothstep(size + .005, size - .005, d);
}


vec3 Flag(vec2 uv,vec2 fragCoord ) {
    float y = sin(uv.y*3.1415*13.);
    float w = fwidth(y);
    w = 0.01;
    vec3 col = RED;

    col = mix(col, YELLOW, Star(uv, BIGSTARTSIZE,vec2(5./30. * iResolution.x / iResolution.y,1.-6./20.),0.));
    col = mix(col, YELLOW, Star(uv, BIGSTARTSIZE/3.,vec2(10./30. * iResolution.x / iResolution.y,1.-2./20.),ROTATO));
    col = mix(col, YELLOW, Star(uv, BIGSTARTSIZE/3.,vec2(12./30. * iResolution.x / iResolution.y,1.-4./20.),-ROTATO));
    col = mix(col, YELLOW, Star(uv, BIGSTARTSIZE/3.,vec2(12./30. * iResolution.x / iResolution.y,1.-7./20.),0.));
    col = mix(col, YELLOW, Star(uv, BIGSTARTSIZE/3.,vec2(10./30. * iResolution.x / iResolution.y,1.-9./20.),ROTATO));
    col *= smoothstep(w, .0, abs(uv.y-.5)-.5+w);
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = uv.x*7.-2.*iTime+uv.y*3.;
    uv.y += sin(t)*.05;

    vec3 col = Flag(uv,fragCoord);

  col *= .7+cos(t)*.3;

    fragColor = vec4(col,1.0);
}
