mat2 rot(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}



vec2 noise2(in vec2 p){

    vec2 res=vec2(0.);
    float f=2.;
	for( int i=0; i< 5; i++ ) 
	{		
        p*=rot(f);     
        f*=1.4;
        res+=sin(p+sin(2.*p.yx));
	}        	
	return res/3.;
}

float dseg( vec2 ba, vec2 pa )
{

	float h = clamp( dot(pa,ba)/dot(ba,ba),0., 2. );	
	return length( pa - ba*h );
}

float arc(vec2 x,vec2 p, vec2 dir)
{
    vec2 r = p;
    float d=10.;
    for (int i = 0; i < 15; i++)
    {
        vec2 s= noise2(r+iTime)+dir;
        d=min(d,dseg(s,x-r));
        r += s;      
    }
    return d*3.;

}



float lightning(vec2 x,vec2 tgt,float dt)
{
    vec2 r = tgt+5.*sin(tgt+.3*iTime);
    float d=1000.;
    float dist=length(tgt-x);

    for (int i = 0; i < 9; i++)
    {
        if(r.y>x.y+.5)break;
        vec2 s= (noise2(r)+vec2(0.,.7))*4.;
        dist = dseg(s,x-r);
        d=min(d,dist);

        r +=s;
        if(i-(i/5)*5==0){
            if(i-(i/10)*10==0)d=min(d,arc(x,r,vec2(.3,.5)));
            else d=min(d,arc(x,r,vec2(-.3,.5)));
        }
    }
    float a = (fract(iTime)>.4)?1.:0.3;
    return a*max(fract(dt-.2*iTime+x.y/100.)-.3,0.)*exp(-8.*d)+a*max(fract(dt+.1-.2*iTime)-.3,0.)*exp(-.2*length(x-tgt));

}




void mainImage( out vec4 C, in vec2 FC )
{
    float o,c,i,e,f,s,g,k=1.;
    vec2 r = iResolution.xy;
    float t = iTime;


    for(c++;i++<1e2;g+=min(f,max(.09,e))*.06*k,k*=1.002)
        {s=2.;vec3 p=vec3((FC.xy-r/s)/r.y*g,g-s);
        p.y = 1.-p.y;
        p.yz*=rot(-.8);
        p.z+=.1*t;
        for(e=f=p.y;s<2e2;s*=1.4)
            p.xz*=rot(s),
            e+=abs(dot(sin(p*s-.2*t)/s,p-p+.3)),
            f+=abs(dot(sin(p*s*.6+2.9+.2*t)/s,p-p+.4));


        }



    o = 1.+ e;
    o*=.5;
    C = vec4(o*o*o, o*o, o,1.);
    vec2 uv =(FC.xy-r/2.)/r.y;
    uv=(1.-uv)*25.;

    vec2 n = noise2(uv+.3*noise2(4.*uv))*.7;
    c = lightning(uv+n,vec2(15.,10.),.4);
    C.rgb += vec3(c, c*c, c+c*c)*4.;

}

#define tau 6.2832

float fbm(float t){
    float s=0.;
    float a = 16.*exp(-3.*t)*t;
    for (float f = 20.;f<1500.;f *=1.35){
        s+=sin(tau*f*(t+2.2))/f;
    }
    return a*s;
}

float fbm2(float t){
    float s=0.;
    float a = 16.*exp(-2.*t)*t;
    for (float f = 1.,d=0.;f<2.;f *=1.05, d+=.2){
        s+=fbm(fbm(t)*f+d)/f;
    }
    return s/15.;
}


vec2 mainSound(int samp, float time) {

    float t = mod(time, 5.0);
    return vec2(fbm2(t));
}
