#define TAU 6.2831853071795862 // 2*PI
#define MIN_FREQ 1.0           // in Hertz
#define R iResolution
#define MX (iMouse.x / R.x)

vec2 sine_distortion(
    float u,
    float v,
    float uAmplitude,
    float vAmplitude,
    float uFrequency,
    float vFrequency)
{
    return vec2(
        u + uAmplitude * sin((TAU * v) * uFrequency),
        v + vAmplitude * sin((TAU * u) * vFrequency));
}

void mainImage( out vec4 O, in vec2 P )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = P/R.xy;

    //float freq = (MIN_FREQ + 1000.0); // noisy blur; ty on iChannel0
    //float freq = (MIN_FREQ + 50.0 * MX); // With mouse control
    float freq = 30.0;

    for (int i = 0; i < 45; ++i)
    {
        uv = 
            sine_distortion(
                uv.x,
                uv.y,
                0.001,
                0.001,
                freq,
                freq);
    }

    // Output to screen
    O = texture(iChannel1, uv);
}
