#define R iResolution

float s(float r, float c) {
    return smoothstep(r, r+1.5/R.y, c);
}

void mainImage( out vec4 O, in vec2 I )
{
    vec2 uv = (I-0.5*R.xy)/R.y;
    if (uv.y > 0.) uv.x *= -1.;
    int steps = 7;
    float maxR = .4;
    float minR = maxR/float(steps);
    float o = 0.;
    for (int i=0; i<steps; i++) {
        float t = float(i)/float(steps);
        float r = minR + maxR * t;
        float c = length(uv + vec2(minR - maxR * (1.-t), 0.));
        c = 1. - s(r, c);
        o += c * minR*2.;        
    }

    vec3 b = vec3(mix(vec3(1.,0.859,0.859), vec3(1., 0., 0.), o));
    vec3 col = mix(vec3(1.000,0.922,0.949), b, 1. - s(maxR, length(uv)));
    O = vec4(col,1.0);
}
