//scene rotate
vec2 rotate(vec2 xy, float a) {
  return vec2(xy.x*cos(a)-xy.y*sin(a),xy.x*sin(a)+xy.y*cos(a));
}
//start
void mainImage(out vec4 color,in vec2 coord){
  float R=32.0;
  //click mouse to change speed and turn time vector
  float SPEED=(iMouse.x-iResolution.x/2.0)/R/2.0;
  if(iMouse.x==iMouse.y&&iMouse.x==0.0)SPEED=-4.0;
  //gravitational wave
  vec2 xy = coord-iResolution.xy/2.0;
  float jTime=iTime*SPEED;xy=rotate(xy,jTime);//universe depends on time
  float r=sqrt(xy.x*xy.x+xy.y*xy.y);float spiral=xy.x*cos(r/R)+xy.y*sin(r/R);
  color=vec4(abs(spiral/r),abs(spiral/r),0.0,0.0);
  if(spiral<0.0)color=vec4(abs(spiral/r),0.0,0.0,0.0);
  if(color.x==color.y)color=vec4(0,abs(spiral/r),0.0,0.0);
  if(abs(color.y-color.x)<0.5&&abs(color.y-color.x)>0.0){color*=8.0;color-=0.25;}
  //massive spheres
  vec2 XY=xy;vec2 YX=xy;float A=cos(iTime)+R/1.5; float B=R/4.0+sin(iTime);
  xy.x=XY.x-A;xy.y=XY.y-B;float r1=sqrt(xy.x*xy.x+xy.y*xy.y);
  xy.x=YX.x+A;xy.y=YX.y+B;float r2=sqrt(xy.x*xy.x+xy.y*xy.y);
  if ((r1<R/3.25||r2<R/3.25)) {color=vec4(1.0);/*vec4(r1<R/3.25,r2<R/3.25,1,0);vec4(int(r2<R/3.0||r1<R/3.0),int(r2<R/3.0),0,0);*/}
  else if (r1<R/2.25||r2<R/2.25) {color/=2.0;}
  //bottom scale
  if(coord.y<20.0)color/=4.0;//it is your clock at this universe
  if(coord.y<15.0&&mod(coord.x-jTime*3.14159265359*2.0,180.0)>=178.0)color=vec4(1);
  if(coord.y<10.0&&mod(coord.x-jTime*3.14159265359*2.0,18.0)>=16.0)color=vec4(1);
  if(coord.y<5.0&&mod(coord.x-jTime*3.14159265359*2.0,9.0)>=7.0)color=vec4(1);
}
