// IMPORTANT: Load a custom image using instructions here: https://www.shadertoy.com/view/lsGGDd
// In a nutshell: Press F12 to open browser devtools, select Console tab, paste this:
//
// gShaderToy.SetTexture(0, {mSrc:'https://dl.dropboxusercontent.com/s/3d1ixs1jnkmkdwg/o5.jpg?dl=0', mType:'texture', mID:1, mSampler:{ filter: 'mipmap', wrap: 'repeat', vflip:'true', srgb:'false', internal:'byte' }});
//
// Change the image URL for any other you want (not every URL works), press ENTER, done.

#define SPEED 4.
#define GLOW_INTENSITY 10.

float sin01(float value) {
  return (sin(value) + 1.) * .5;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord/iResolution.xy;

  vec3 color = vec3(texture(iChannel0, uv));
  vec3 bloom = color * sin01(iTime * SPEED) * GLOW_INTENSITY;

  fragColor = vec4(color + bloom, 1.);
}
