// See define in common

float plotSmoothLine(
    float value, // in range [-1.0; 1.0]
    float v,     // in range [0.0; 1.0]
    float width) // > 0.0
{
    return 
        smoothstep(
            0.0,
            width / iResolution.y,
            abs(v - (1.0 + value) * 0.5));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    uv.x *= 0.05;

    float gray = plotSmoothLine(
        additiveWave(VOLUME, BASE_FREQUENCY, (uv.x + iTime)),
        uv.y,
        2.0);

    // Output to screen
    fragColor = vec4(vec3(gray),1.0);
}

// See common

vec2 mainSound( int samp, float time)
{
    //float level = sineOsc(VOLUME, BASE_FREQUENCY, time, 0.0);
    float level = additiveWave(VOLUME, BASE_FREQUENCY, time);

    return vec2(level, level);
}

#define TAU 6.2831853071795862 // 2*PI
#define BASE_FREQUENCY 60.0    // in Hertz
#define VOLUME 0.5             // in range [0.0; 1.0]

float sineOsc(
    float amplitude,   // in range [0.0; 1.0]
    float frequency,   // > 0.0
    float time,        
    float phase_shift)
{
    float phase = phase_shift + time * TAU * frequency;

    return amplitude * sin(phase);
}

float additiveWave(
    float amplitude,
    float baseFrequncy,
    float time)
{
    float level = 
             sineOsc(1.0, baseFrequncy,       time, 0.0)  * 0.75;
    level += sineOsc(1.0, baseFrequncy * 2.0, time, 0.0)  * 0.25;
    level += sineOsc(1.0, baseFrequncy * 3.0, time, 0.0)  * 0.05;
    level += sineOsc(1.0, baseFrequncy * 8.0, time, 0.0)  * 0.02;
    //                                                      sum = 1.07

    return (level / 1.07) * amplitude;
}
