#define ITR 128
#define EPS 0.001

mat2 rot(float angle){
	float s=sin(angle);
	float c=cos(angle);
	return mat2(s,-c,c,s);
}

float sdSphere(vec3 p){
	return length(p)-1.0;
}

//from iq
float displacement(vec3 p){
	return sin(10.0*p.x)*sin(10.0*p.y)*sin(10.0*p.z)*sin(iTime);
}

float mainDist(vec3 p){
	p.xy*=rot(iTime*0.5);
	p.xz*=rot(iTime*0.5);
	float d1=sdSphere(p);
	float d2=displacement(p);
	return d1+d2;
}

//generate normal of sphere
vec3 genNormal(vec3 p){
	return normalize(p);
}



void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 uv=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
	vec3 ro=vec3(0.0,0.0,-9.0);
	vec3 lookat=vec3(0.0,0.0,0.0);
	float zoom=5.0;

	vec3 f=normalize(lookat-ro);
	vec3 r=normalize(cross(vec3(0.0,1.0,0.0),f));
	vec3 u=cross(f,r);
	vec3 c=ro+f*zoom;
	vec3 i=c+uv.x*r+uv.y*u;
	vec3 rd=normalize(i-ro);

    float d,t;
    vec3 p,n;

	vec3 col=vec3(0.0);

    for(int i=0;i<ITR;i++){
        p=ro+rd*t;
        d=mainDist(p);
        n=genNormal(p);
        if(d<EPS)break;
        t+=d;
    }
	col=n*0.5+0.5;

	fragColor=vec4(col,1.0);
}
