// See https://www.desmos.com/calculator/7rpupvhbh9

// SDFs
float sdEllipse(in vec2 p, in vec2 ab) {
    p = abs(p);
    if (ab.y > ab.x) p = p.yx, ab = ab.yx;

    vec2 pab = p * ab;
    float s1 = ab.x * ab.x - ab.y * ab.y;
    float s2 = length(pab);
    float offs = atan(pab.x / pab.y);

    float t = 1.1;
    for (int n=0; n < 10; n++) {
        float to = t + offs, t2 = 2.0 * t;
        float y = 0.5 * s1 * sin(t2) + s2 * cos(to);
        t -= y / (s1 * cos(t2) - s2 * sin(to));
        if (abs(y) < 1e-7 * (s2 + s1)) break;
    }

    pab = p / ab;
    return length(p - vec2(cos(t), sin(t)) * ab) * sign(dot(pab, pab) - 1.0);
}

#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(0.0);

    float ang = texelFetch(iChannel0, ivec2(0), 0).x;
    float co = cos(ang), si = sin(ang);
    mat2 rot = mat2(co, si, -si, co);

    float ellip = sdEllipse(rot * uv + AXIS, AB);
    drawSDF(ellip, vec3(1.0, 0.0, 0.0));
    drawSDF(abs(ellip), vec3(1.0, 0.8, 0.0));
    drawSDF(length(uv) - 0.05, vec3(1.0, 0.8, 0.0));

    float aspect = iResolution.x / iResolution.y;
    color = digitIn(color, vec3(1.0), uv * 0.25 + vec2(0.45 * aspect, 0.45), 100.0, ellipseInertia(AB, AXIS));

    fragColor = vec4(color, 1.0);
}

// Constants
#define PI 3.14159265359

// Ellipse settings
#define AB (iTime > 12.0 ? vec2(1.75, 0.75) + 0.25 * sin(iTime - 12.0) : vec2(1.75, 0.75))
#define AXIS vec2(-0.75, 0.25)
#define DENSITY 0.1

// Simulation settings
#define FRICTION 0.98
#define GRAVITY 0.2

// cross3D(vec3(a.x, a.y, 0), vec3(b.x, b.y, 0))
// Since the x and y components of the result are always zero
// The z component is the only one of interest
float cross2D(in vec2 a, in vec2 b) {
    return a.x * b.y - a.y * b.x;
}

// Mass of an ellipse with uniform density
float ellipseMass(in vec2 ab, in float density) {
    return PI * ab.x * ab.y * density;
}

// Moment of inertia of an ellipse with dimensions ab about axis p
float ellipseInertia(in vec2 ab, in vec2 p) {
    return (dot(ab, ab) / 4.0 + dot(p, p)) * PI * ab.x * ab.y;
}

