const float pi = 3.14159;
const float tan3 = tan(pi/3.0);
const float innerRad = sqrt(3.0) / 6.0;
vec3 getTriangleColor(vec2 uv, float ang, vec3 col, float size)
{
    vec2 tuv = vec2(uv.x * cos(ang) + uv.y * sin(ang), -uv.x * sin(ang) + uv.y * cos(ang));
    return col * float(tuv.y + innerRad < (tuv.x + size) * tan3 + tan3 / 2.0 && tuv.y + innerRad < -(tuv.x - size) * tan3 + tan3 / 2.0 && tuv.y + innerRad > -size);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    const float shift = 0.11;
    const float sizeShift = 0.02;
    fragCoord -= iResolution.xy / 3.0;
    vec2 uv = fragCoord/iResolution.x*4.0;
    vec3 col = vec3(0.02,0.0176,0.1);
    float ang;
    fragColor = vec4(0.0,0.0,0.0,1.0);
    for (float i = 0.0; i < 60.0; i += 1.0)
    {
        ang = sin(iTime + shift * i);
        fragColor.rgb += getTriangleColor(uv,ang,col,sizeShift*i - sizeShift*15.0*sin(iTime*0.3));
    }
}
