void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = vec2(0, 0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec2 d = uv - center;
    float dis = dot(d,d);
    if(dis < 0.5)
        fragColor.x = 1.0;

}
