// Extruded version of https://www.shadertoy.com/view/ftcfWf Asymmetric Blocks SDF.

// Thanks to Shane for the idea explained in https://www.shadertoy.com/view/Ws3GRs Asymmetric Blocks :)


#define EPSILON 0.001
#define DIST_MAX 50.0

// https://www.shadertoy.com/view/WttXWX "Best" Integer Hash by FabriceNeyret2,
// implementing Chris Wellons https://nullprogram.com/blog/2018/07/31/
uint triple32(uint x)
{
    x ^= x >> 17;
    x *= 0xed5ad4bbU;
    x ^= x >> 11;
    x *= 0xac4c1b51U;
    x ^= x >> 15;
    x *= 0x31848babU;
    x ^= x >> 14;
    return x;
}

float hash(uint x)
{
    return float(triple32(x)) / float(0xffffffffU);
}

float hash(uvec2 v)
{
    //return hash(v.x + triple32(v.y));
    return mix(0.2, 0.8, hash(v.x + triple32(v.y)));
}

float box( in vec2 p, in vec2 b ) // https://iquilezles.org/articles/distfunctions2d/ by iq
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdRoundedBox( in vec2 p, in vec2 b, in float r ) // https://iquilezles.org/articles/distfunctions2d/
{
    vec2 q = abs(p)-b+r;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r;
}

float box2(vec2 p, vec2 p0, vec2 p1) // min-max box
{
    //return box(p-(p1+p0)/2.0, (p1-p0)/2.0);
    return sdRoundedBox(p-(p1+p0)/2.0, (p1-p0)/2.0, 0.1);
}

// Idea from https://www.shadertoy.com/view/Ws3GRs Asymmetric Blocks by Shane.
// Checkerboard tiling of alternating (horizontal vs. vertical) (double-T-) crossings.
// Implementation my own since I prefer writing code to reading code ;-)
float blocks(in vec2 p)
{
    vec2 q = fract(p); // local coordinates inside tile
    p = floor(p); // global coordinates of tile
    ivec2 tile = ivec2(p);
    //bool flip = (tile.x % 2) == (tile.y % 2); // XXX breaks on windows XXX
    bool flip = (tile.x & 1) == (tile.y & 1);
    if(flip) // vertical line goes through
    {
        float x = hash(uvec2(tile)); // x-coordinate of vertical line through this tile
        float xsw = hash(uvec2(tile+ivec2(-1,-1))); // x-coordinate of vertical line through south-west tile
        float xnw = hash(uvec2(tile+ivec2(-1,+1))); // x-coordinate of vertical line through north-west tile
        float xse = hash(uvec2(tile+ivec2(+1,-1))); // x-coordinate of vertical line through south-east tile
        float xne = hash(uvec2(tile+ivec2(+1,+1))); // x-coordinate of vertical line through north-east tile
        float yw = hash(uvec2(tile+ivec2(-1, 0))); // y-coordinate of horizontal line through west tile
        float ye = hash(uvec2(tile+ivec2(+1, 0))); // y-coordinate of horizontal line through east tile
        float ys = hash(uvec2(tile+ivec2( 0,-1))); // y-coordinate of horizontal line through south tile
        float yn = hash(uvec2(tile+ivec2( 0,+1))); // y-coordinate of horizontal line through north tile

        //return box2(q, vec2(xsw-1.0, ys-1.0), vec2(x, yw)); // south-west
        //return box2(q, vec2(xnw-1.0, yw), vec2(x, yn+1.0)); // north-west
        //return box2(q, vec2(x, ys-1.0), vec2(xse+1.0, ye)); // south-east
        //return box2(q, vec2(x, ye), vec2(xse+1.0, yn+1.0)); // north-east

        return
            min
            (
                min
                (
                    box2(q, vec2(xsw-1.0, ys-1.0), vec2(x, yw)), // south-west
                    box2(q, vec2(xnw-1.0, yw), vec2(x, yn+1.0)) // north-west
                ),
                min
                (
                    box2(q, vec2(x, ys-1.0), vec2(xse+1.0, ye)), // south-east
                    box2(q, vec2(x, ye), vec2(xne+1.0, yn+1.0)) // north-east
                )
            );
    }
    else // horizontal line goes through
    {
        float y = hash(uvec2(tile)); // x-coordinate of horizontal line through this tile
        float ysw = hash(uvec2(tile+ivec2(-1,-1))); // y-coordinate of horizontal line through south-west tile
        float ynw = hash(uvec2(tile+ivec2(-1,+1))); // y-coordinate of horizontal line through north-west tile
        float yse = hash(uvec2(tile+ivec2(+1,-1))); // y-coordinate of horizontal line through south-east tile
        float yne = hash(uvec2(tile+ivec2(+1,+1))); // y-coordinate of horizontal line through north-east tile
        float xw = hash(uvec2(tile+ivec2(-1, 0))); // x-coordinate of vertical line through west tile
        float xe = hash(uvec2(tile+ivec2(+1, 0))); // x-coordinate of vertical line through east tile
        float xs = hash(uvec2(tile+ivec2( 0,-1))); // x-coordinate of vertical line through south tile
        float xn = hash(uvec2(tile+ivec2( 0,+1))); // x-coordinate of vertical line through north tile

        //return box2(q, vec2(xw-1.0, ysw-1.0), vec2(xs, y)); // south-west
        //return box2(q, vec2(xw-1.0, y), vec2(xn, ynw+1.0)); // north-west
        //return box2(q, vec2(xs, yse-1.0), vec2(xe+1.0, y)); // south-east
        //return box2(q, vec2(xn, y), vec2(xe+1.0, yne+1.0)); // north-east

        return
            min
            (
                min
                (
                    box2(q, vec2(xw-1.0, ysw-1.0), vec2(xs, y)), // south-west
                    box2(q, vec2(xw-1.0, y), vec2(xn, ynw+1.0)) // north-west
                ),
                min
                (
                    box2(q, vec2(xs, yse-1.0), vec2(xe+1.0, y)), // south-east
                    box2(q, vec2(xn, y), vec2(xe+1.0, yne+1.0)) // north-east
                )
            );
    }
}

float extrude_blocks(vec3 p)
{
    float dp = p.z; // distance to plane
    float df = blocks(p.xy)+0.05; // distance to 2d SDF
    //return max(df, dp); // 3dify - not an exact distance field

    // Correct SDF, as suggested by iq - thanks!
    vec2 w = vec2(df, dp);
    return min(max(w.x, w.y), 0.0) + length(max(w, 0.0));

    //return min(max(df, dp), 0.0) + length(max(vec2(df, dp), 0.0)); // rewritten to compare to incorrect approach
}

float halfspace(vec3 p, float d)
{
    return p.z - d;
}

float map(vec3 p)
{
    float d = mix(0.01, 0.1, 0.5 + 0.5 * cos(iTime));
    return
        min
        (
            //extrude_blocks(p),
            extrude_blocks(p) - 0.1, // TEST: With offset incorrect SDF retains sharp edges while correct SDF aquires rounded edges.
            halfspace(p, -1.0)
        );
}

// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p)
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return
        normalize
        (
            k.xyy * map(p + k.xyy*h)
            +
            k.yyx * map(p + k.yyx*h)
            +
            k.yxy * map(p + k.yxy*h)
            +
            k.xxx * map(p + k.xxx*h)
        );
}

#define ITER_MAX 250u

float trace(vec3 ro, vec3 rd)
{
    float t;
    uint i;
    for(i = 0u, t = 0.0; i < ITER_MAX && t < DIST_MAX; i++)
    {
        float h = map(ro + rd * t);
        if(h < EPSILON)
            return t;
        t += h;
    }
    return DIST_MAX;
}

// https://iquilezles.org/articles/rmshadows
float shadow( in vec3 ro, in vec3 rd, float mint, float maxt )
{
    float t;
    uint i;
    for(i = 0u, t = mint; i < ITER_MAX && t<maxt; i++)
    {
        float h = map(ro + rd*t);
        if(h < EPSILON)
            return 0.0;
        t += h;
    }
    return 1.0;
}

// https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor );
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

#define pi 3.1415926

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 ndc = 2.0 * uv - 1.0;
    ndc.x *= float(iResolution.x) / float(iResolution.y);
    float mx = 2.0 * pi * float(iMouse.x) / float(iResolution.x);
    float my = pi / 2.0 + pi / 2.0 * float(iMouse.y) / float(iResolution.y);
    mx = (iMouse.x > 10.0) ? mx : 2.0 * pi * fract(iTime * 0.01);
    my = (iMouse.y > 10.0) ? my : pi * 3.0 / 4.0;;

    mat2 R = mat2(vec2(cos(mx), sin(mx)), vec2(-sin(mx), cos(mx)));
    vec3 ro = vec3(0.0, 0.0, -5.0 );//vec3(0.0, -10.0 * my, 0.0);
    //mat2 S = mat2(vec2(0.0, 1.0), vec2(-1.0, 0.0));
    mat2 S = mat2(vec2(cos(my), sin(my)), vec2(-sin(my), cos(my)));
    ro.yz=S*ro.yz;
    ro.xy = R * ro.xy;

    vec3 rd = normalize(vec3(0.5 * ndc.xy, 1.0)); // NOTE: omitting normalization results in clipped edges artifact
    rd.yz=S*rd.yz;
    rd.xy = R * rd.xy;

    float dist = trace(ro, rd);
    vec3 dst = ro + rd * dist;
    vec3 n = normal(dst);

    vec3 lightdir = normalize(vec3(1.0, 1.0, 1.0));
    vec3 ambient = vec3(0.05);
    float brightness = max(dot(lightdir, n), 0.0);
    brightness *= shadow(ro+rd*dist,lightdir, 0.01, DIST_MAX); // XXX artifacts on cylinder XXX
    vec3 color = texture(iChannel0, dst.xy).xxx;
    //vec3 color = vec3(1.0);
    //color *= (n * 0.5 + 0.5);
    //color = (ambient * calcAO(dst, n) + brightness) * color;
    color = (ambient + brightness) * color;

    fragColor = mix(vec4(color, 1.0), vec4(0.0), step(DIST_MAX, dist));
    fragColor = sqrt(fragColor); // approximate gamma
}
