vec3 blackbody(float T){
    //modded @fabrice /4tdGWM
    vec3 O;
    count(3){
        float f = 1.+.5*float(n); 
        O[n]= f*f*f/(exp(19E3*f/T)-1.);
    }
    return O;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    //basis
    vec2 uv = fc/res;
    vec2 uvn= nmaps(uv);
    uvn+= perlin(vec3(vec2(time*.3),0.))*.05*vec2(1.,.2);//camera shake
    uvn+= vec2(-.9,-.8);
    uvn.x*= asp;
    uvn= mat2(
        -1, 1,
         1, 1
    )*uvn;
    uvn+= perlin(vec3(vec2(0.,time*4.)+uvn*vec2( 64.,128.),time*4.))*.02;
    uvn*= 24.;

    //cone
    float x= uvn.x*.5;
    float y= -uvn.y;
    float l= -x*x+y;
    //l= smooth(l);    

    //mass
    float m= 1569.;
    float e= y*y+.125;//expansion
    float k= sat(abs(x*x*x*.2)/(y*y));//spike
    k= max(.001,k);
    float d= 0.;//pow(nmapu(sin(80.*(y)/e)),20.) * sat(1.-s*8100.);//shock diamond

    l*= m*(k+d)/e;

    //turbulence, fucking shit hueristic fixme
    float R= 
        +1.4
        -pow(perlin(vec3(vec2(0.,time*2.)+uvn*vec2( .7,4.),time*80.5/e)),.5 )*.8
        +pow(perlin(vec3(vec2(0.,time*2.)+uvn*vec2(14.,1.4),time*180.5/e )),2.)*1.
        ;
    l*= max(0.8,R);
    //l=R;


    //irradiance
    float T= 3400.*sat(1.-pow(y*.0004,1./4.));//heat; mass invariant
    float I= 10./(exp(1.5/T)-1.);//planck's law; omits wavelength; wiki/Brightness_temperature

    //vec3 c= vec3(l);
    vec3 c= vec3(blackbody(T)*I)*l;
    //vec3 c= blackbody(uv.x*3300.)*100.;

    //tonemap
    c= maxs(0.,c);
    c+= SKY;
    c*= Ev;
    c= 1.-1./(c+1.);
    float cm= maxv(c);
    c/= max(1.,cm);
    c= srgb(c);
    fragColor = vec4(c,1.);
}

