float selfSimilar(float r, float offset, float smear) {
    offset = mod(offset, 1.0);
    float r2 = mod(offset+log2(r)/smear, 1.0);
    return pow(2.0, abs(r2-0.5));
}

mat2 rotation(float angle) {
    float s = sin(angle), c = cos(angle);
    return mat2(c, s, -s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mid = vec2(0.5);

    float time = iTime * 0.5;

    vec2 dp = uv-mid;
    float r = length(dp)*3.0; // twist strength
    vec2 dir = dp/r;

    float r1 = selfSimilar(r, 0.5*time, 2.5);
    mat2 rot1 = rotation(0.2/r);
    vec4 col1 = vec4(texture(iChannel0, mid+rot1*r1*dir));

    float r2 = selfSimilar(r, 1.3*time, 2.0);
    mat2 rot2 = rotation(iTime*0.2+0.2/r);
    vec4 col2 = vec4(texture(iChannel0, mid-rot2*r2*dir));

    fragColor = mix(col1, col2, 0.5);
}
