void mainImage( out vec4 j, in vec2 J )
{
    vec2 q,r = iResolution.xy;
    vec4 c,z = vec4(q , (J.x*2.4-1.5*r.x)/r.y , J.y/r.y*2.4-1.2);
    int t,i=t=0;
    for (;i<98 && z.x*z.x+z.y*z.y < 4.0;i++,t++)
        z = vec4(z.x*z.x-z.y*z.y+z.z,2.0*z.x*z.y+z.w,z.zw);
    c = vec4(t>97?0.0:float(t)/50.0);
    j=c;
}
