vec2 triangle_wave(vec2 a){
    return abs(fract((a+vec2(1.,0.5))*1.5)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 4.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    bool t3 = false;
    for(int k = 0; k < 9; k++){
        t3 = uv.x > uv.y || !t3;
        //t3 = !t3 && uv.x > uv.y || t3 && uv.x < uv.y;
        //t3 = !(t3 || uv.x < uv.y) && (!t3 || uv.x > uv.y);
        uv = (uv+t2)/scale;
        t2 = triangle_wave(uv+.5);
        //t2 = abs(t2);

        uv = t2-triangle_wave(uv.yx);
        float c1 = uv.y-uv.x;
        //c1 = max(c1,t2.x-t2.y);
        col = vec3(c1,col.yz);

        //col = col.xzy;
        if(t2.x < t2.y && t3) col = col.yzx;
        //if(t2.x > t2.y && !t3) col = col.zxy;
        //if(t2.x*uv.x < t2.y*uv.y) col = col.yzx;
        //if(t2.x-t2.y > uv.x-uv.y) col = col.yzx;


    }
    fragColor = vec4(abs(col)*2.,1.0);
}
